package club.dlblog.encrypt.rsa;import java.util.Map;import java.util.concurrent.ConcurrentHashMap;/** * @author machenike */public class LocalKeyStore implements KeyStore {    private Map<String,String> keyMap = new ConcurrentHashMap<>();    @Override    public void setKeyPair(String publicKey, String privateKey) {        keyMap.put(publicKey,privateKey);    }    @Override    public String getPrivateKey(String publicKey) {        return keyMap.get(publicKey);    }    @Override    public void clean() {        keyMap.clear();    }}